DROP TABLE IF EXISTS book;
CREATE TABLE book
(
    id          BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, -- 自增主键
    name        VARCHAR(100) NOT NULL,                           -- 书名
    author      VARCHAR(100) NOT NULL,                           -- 作者
    category_id BIGINT       NOT NULL,                           -- 所属分类ID
    image       VARCHAR(255),                                    -- 封面图片URL
    description TEXT,                                            -- 书籍描述
    status      INTEGER      NOT NULL DEFAULT 1,                 -- 状态（1=上架, 0=下架）
    create_time TIMESTAMP,                                       -- 创建时间
    update_time TIMESTAMP,                                       -- 更新时间
    stock       BIGINT       NOT NULL,                           -- 库存数量
    isbn        VARCHAR(64)  NOT NULL,                           -- ISBN
    location    VARCHAR(64)  NOT NULL,                           -- 存放位置
    publisher   VARCHAR(64)  NOT NULL                            -- 出版社
);

