DROP TABLE IF EXISTS borrow_cart;
CREATE TABLE borrow_cart
(
    id          BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, -- 主键
    name        VARCHAR(32),                                     -- 书本名称
    image       VARCHAR(255),                                    -- 图片
    user_id     BIGINT NOT NULL,                                 -- 用户ID
    book_id     BIGINT,
    number      INT    NOT NULL DEFAULT 1,                       -- 数量
    create_time TIMESTAMP                                        -- 创建时间
);

DROP TABLE IF EXISTS admin;
CREATE TABLE admin
(
    id       BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, -- 自增主键
    username VARCHAR(32) NOT NULL,                                -- 邮箱
    password VARCHAR(128)                                         -- 密码
);

