mapper.select:
select b .* , c.name as categoryName from book b left outer join category c on d.category_id = c.id


        and b.name like concat('%',#{name},'')


        and b.category_id = #{categoryId}


        and b.status = #{status}


order by b.create_time desc

mapper.update:
update book
name = #{name}, author = #{author}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, stock = #{stock}, isbn = #{isbn}, location = #{location}, publisher = #{publisher}
where id = #{id}

mapper.select:
select * from book


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from book


        and status = #{status}


        and category_id = #{categoryId}
